import { combineReducers } from 'redux'

function Calculator(state = 0, action) {
    switch (action.type) {
        case 'Add':
            return state + 1
        case 'Subtract':
            return state - 1
        case 'Multiple':
            return state * 1
        case 'Equal':
            return state - 1
        case '1':
            if (state === 0) {
                return "1"
            }
            else {
                return state + "1"
            }
        case '2':
            if (state === 0) {
                return "2"
            }
            else {
                return state + "2"
            }
        default:
            return state
    }
}

const reducers = combineReducers({
    value: Calculator,
})

export default reducers
