import { combineReducers } from 'redux'

function Calculator(state = 0, action) {
    switch (action.type) {
        case 'Add':
            return state + 1
        case 'Subtract':
            return state - 1
        case 'Multiple':
            return state * 1
        case 'Equal':
            return state - 1
        default:
            if (state === 0) {
                return action.type
            }
            else {
                return state + "1"
            }
    }
}

const reducers = combineReducers({
    value: Calculator,
})

export default reducers
