import { combineReducers } from 'redux'

const initialState = { value: '0', symbole: null, value2: null }


function Calculator(state = initialState, action) {
    switch (action.type) {
        case 'Add':
            return {
                value2: value,
                symbole: '*',
                value: value,
            }
        case 'Subtract':
            return state.value - 1
        case 'Multiple':
            return state.value * 1
        case 'Equal':
            return state.value - 1
        case 'Clear':
            return '0'
        case 'Del':
            if (state.value.length === 1) {
                return '0'
            }
            else {
                return state.value.substr(0, state.length - 1)
            }
        case '1':
            if (state.value === 0) {
                return "1"
            }
            else {
                return state.value + "1"
            }
        case '2':
