                return state.value + "7"
            }
        case '8':
            if (state.value === 0) {
                return "8"
            }
            else {
                return state.value + "8"
            }
        case '9':
            if (state.value === 0) {
                return "9"
            }
            else {
                return state.value + "9"
            }
        case '0':
            if (state.value === 0) {
                return "0"
            }
            else {
                return state.value + '0'
            }
        case '.':
            if (state.value.indexOf(".") >= 0) {
                return state
            }
            else {
                return state.value + '.'
            }
        default:
            return state.value
    }
}

const reducers = combineReducers({
    values: Calculator,
})

export default reducers
