import { combineReducers } from 'redux'

const initialState = { value: 0, symbole: null, value2: null }


function Calculator(state = initialState, action) {
    switch (action.type) {
        case 'Add':
            return state + 1
        case 'Subtract':
            return state - 1
        case 'Multiple':
            return state * 1
        case 'Equal':
            return state - 1
        case 'Clear':
            return '0'
        case 'Del':
            if (state.length === 1) {
                return '0'
            }
            else {
                return state.substr(0, state.length - 1)
            }
        case '1':
            if (state === 0) {
                return "1"
            }
            else {
                return state + "1"
            }
        case '2':
            if (state === 0) {
                return "2"
            }
            else {
                return state + "2"
            }
        case '3':
            if (state === 0) {
                return "3"
            }
            else {
                return state + "3"
            }
        case '4':
            if (state === 0) {
                return "4"
            }
            else {
                return state + "4"
            }
        case '5':
            if (state === 0) {
                return "5"
            }
            else {
                return state + "5"
            }
        case '6':
            if (state === 0) {
                return "6"
            }
            else {
                return state + "6"
            }
        case '7':
            if (state === 0) {
                return "7"
            }
            else {
                return state + "7"
            }
        case '8':
            if (state === 0) {
                return "8"
            }
            else {
                return state + "8"
            }
        case '9':
            if (state === 0) {
                return "9"
            }
            else {
                return state + "9"
            }
        case '0':
            if (state === 0) {
                return "0"
            }
            else {
                return state + '0'
            }
        case '.':
            if (state.indexOf(".") >= 0) {
                return state
            }
            else {
                return state + '.'
            }
        default:
            return state
    }
}

const reducers = combineReducers({
    Calculator,
})

export default reducers
