import { combineReducers } from 'redux'

const initialState = { value: '0', symbole: null, value2: null }


function Calculator(state = initialState, action) {
    switch (action.type) {
        case 'Add':
            if (state.value2 === 0) {
                state.value2 = state.value
            }
            return {
                value2: state.value,
                symbole: '*',
                value: '0',
            }
        case 'Subtract':
            return {
                value2: state.value,
                symbole: '*',
                value: '0',
            }
        case 'Multiple':
            return {
                value2: state.value,
                symbole: '*',
                value: '0',
            }
        case 'Equal':
            return state.value - 1
        case 'Clear':
            return '0'
        case 'Del':
            if (state.value.length === 1) {
                return { value: '0', symbole: state.symbole, value2: state.value2 }
            }
            else {
                return {
                    value: state.value.substr(0, state.value.length - 1),
                    symbole: state.symbole,
                    value2: state.value2
                }
            }
        case '1':
            if (state.value === '0') {
                return { value: '1', symbole: state.symbole, value2: state.value2 }
            }
            else {
                return { value: state.value + '1', symbole: state.symbole, value2: state.value2 }
            }
        case '2':
            if (state.value === '0') {
                return { value: '2', symbole: state.symbole, value2: state.value2 }
            }
            else {
                return { value: state.value + '2', symbole: state.symbole, value2: state.value2 }
            }
        case '3':
            if (state.value === '0') {
                return { value: '3', symbole: state.symbole, value3: state.value3 }
            }
            else {
                return { value: state.value + '3', symbole: state.symbole, value3: state.value3 }
            }
        case '4':
            if (state.value === '0') {
                return { value: '4', symbole: state.symbole, value4: state.value4 }
            }
            else {
                return { value: state.value + '4', symbole: state.symbole, value4: state.value4 }
            }
        case '5':
            if (state.value === '0') {
                return { value: '5', symbole: state.symbole, value5: state.value5 }
            }
            else {
                return { value: state.value + '5', symbole: state.symbole, value5: state.value5 }
            }
        case '6':
            if (state.value === '0') {
                return { value: '6', symbole: state.symbole, value6: state.value6 }
            }
            else {
                return { value: state.value + '6', symbole: state.symbole, value6: state.value6 }
            }
        case '7':
            if (state.value === '0') {
                return { value: '7', symbole: state.symbole, value7: state.value7 }
            }
            else {
                return { value: state.value + '7', symbole: state.symbole, value7: state.value7 }
            }
        case '8':
            if (state.value === '0') {
                return { value: '8', symbole: state.symbole, value8: state.value8 }
            }
            else {
                return { value: state.value + '8', symbole: state.symbole, value8: state.value8 }
            }
        case '9':
            if (state.value === '0') {
                return { value: '9', symbole: state.symbole, value9: state.value9 }
            }
            else {
                return { value: state.value + '9', symbole: state.symbole, value9: state.value9 }
            }
        case '0':
            if (state.value === '0') {
                return { value: '0', symbole: state.symbole, value0: state.value0 }
            }
            else {
                return { value: state.value + '0', symbole: state.symbole, value0: state.value0 }
            }
        case '.':
            if (state.value.indexOf(".") >= 0) {
                return state
            }
            else {
                return { value: state.value + '.', symbole: state.symbole, value0: state.value0 }
            }
        default:
            return state
    }
}

const reducers = combineReducers({
    values: Calculator,
})

export default reducers
