import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Button, Container, Row, Col, ButtonToolbar, ButtonGroup } from 'react-bootstrap';

class Calculator extends Component {
    render() {
        return (
            <Container>
                <Row>
                    <Col>{this.props.value}</Col>
                </Row>
                <Row>
                    <ButtonToolbar aria-label="Toolbar with button groups">
                        <ButtonGroup className="mr-2 full-width" aria-label="First group">
                            <Button onClick={this.props.add}>C</Button>
                        </ButtonGroup>
                    </ButtonToolbar>
                </Row>
                <Row>
                    <ButtonToolbar aria-label="Toolbar with button groups">
                        <ButtonGroup className="mr-2" aria-label="First group">
                            <Button onClick={this.props.add}>+</Button>
                            <Button onClick={this.props.subtract}>-</Button>
                            <Button onClick={this.props.subtract}>*</Button>
                            <Button onClick={this.props.subtract}>/</Button>
                        </ButtonGroup>
                    </ButtonToolbar>
                </Row>
            </Container>
        )
    }
}

function mapStateToProps(state) {
    return {
        value: state.counter
    }
}

function mapDispatchToProps(dispatch) {
    return {
        add: () => {
            dispatch({ type: 'Add' })
        },
        subtract: () => {
            dispatch({ type: 'Subtract' })
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Calculator)
